import React, { Component } from "react";
import axios from "axios";
import jwt_decode from "jwt-decode";

import { withRouter } from "react-router-dom";
class Login extends Component {
  state = {
    email: "",
    password: ""
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  login = () => {
    const { email, password } = this.state;
    axios
      .post("http://localhost:7000/api/users/login", { email, password })
      .then(res => {
        localStorage.setItem("token", res.data.token);
        var token = res.data.token;
        var decoded = jwt_decode(token);
        console.log(decoded);

        if (decoded.typeuser === "client") this.props.history.push("/client");
        else this.props.history.push("/home");
      })
      .catch(err =>
        // console.error(err)
        alert("Please verify your login & password")
      );
  };

  render() {
    return (
      <div style={{ margin: "10px" }}>
        <h3>Login Page</h3>
        <input
          type="text"
          placeholder="Please type your email"
          name="email"
          onChange={e => this.handleChange(e)}
          value={this.state.email}
        />
        <input
          type="password"
          placeholder="Please type your password"
          name="password"
          onChange={e => this.handleChange(e)}
          value={this.state.password}
        />
        <button onClick={() => this.login()}>Login</button>
        <div className='from-register'>
<h1 className='title'>Identification</h1>
<form>
  <div class="form-group row">
    <label for="inputEmail3" class="col-sm-2 col-form-label">Email</label>
    <div class="col-sm-4">
      <input type="email" class="form-control" id="inputEmail3"  onChange={e => this.handleChange(e)}
          value={this.state.email}/>
    </div>
  </div>
  <div class="form-group row">
    <label for="inputPassword3" class="col-sm-2 col-form-label">Password</label>
    <div class="col-sm-4">
      <input type="password" class="form-control" id="inputPassword3" onChange={e => this.handleChange(e)}
          value={this.state.password}/>
    </div>
  </div>
  <fieldset class="form-group">
    <div class="row">
      <legend class="col-form-label col-sm-2 pt-0">Account</legend>
      <div class="col-sm-4">
        <div class="form-check">
          <input class="form-check-input" type="radio" name="gridRadios" id="gridRadios1" value="option1" checked/>
          <label class="form-check-label" for="gridRadios1">
            Worker
          </label>
        </div>
        <div class="form-check">
          <input class="form-check-input" type="radio" name="gridRadios" id="gridRadios2" value="option2"/>
          <label class="form-check-label" for="gridRadios2">
            Client
          </label>
        </div>
      </div>
    </div>
  </fieldset>
  <div class="form-group row">
    <div class="col-sm-4">
      <button type="submit" class="btn btn-danger">Login</button>
    </div>
  </div>
</form>
</div>
